INSERT INTO `xt_form` VALUES ('68','欧金事业优惠活动方案','<p>&nbsp; &nbsp; &nbsp;&nbsp; <span style=\"font-size: xx-large;\">&nbsp; </span><span style=\"font-family: \'Comic Sans MS\';\"><span style=\"font-size: xx-large;\">亲爱的家人们！朋友们：欢迎您了解欧金事业！为了感谢各位会员对公司的信任与支持，经公司董事会决定公司启动期间给予市场以下优惠：</span><span style=\"font-size: xx-large;\"><br />\r\n&nbsp; &nbsp; &nbsp; <span style=\"color: rgb(255, 0, 0);\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;1,2014年10月6日--8日加盟一星享受级为五星所有待遇，9日---11日加盟一星享受四星所有待遇，12日---14日加盟一星享受三星所有待遇;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;2.充值1500美金公司开通报单中心同时6日--8日给予2200美金货币，9日---11日给予2000美金货币，12日---14日给予1800美金货币.</span><br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 本活动结束后按照正常报单充值流程，请大家抓住机会，最终解释权归欧金事业所有，谢谢大家的信任与支持！欧金事业，创造梦想的舞台！</span><span style=\"font-size: xx-large;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span><span style=\"font-size: x-large;\"> &nbsp; &nbsp;欧金事业项目组</span></span></p>\r\n<div><span style=\"font-size: x-large;\"><span style=\"font-family: \'Comic Sans MS\';\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 二0一四年十月五日</span></span></div>','1','1412438757','1412518245','1','0','1');-- <fen> --
INSERT INTO `xt_form` VALUES ('67','欧金事业启动公告','<div>&nbsp;<span style=\"font-family: \'Comic Sans MS\';\"><span style=\"font-size: xx-large;\">亲爱的欧金家人们！首先欢迎各位精英加盟欧金事业复利宝股权理财计划，经公司董事会决定于<span style=\"color: rgb(255, 0, 0);\">2014年10月6日</span>正式启动国内市场。在这里欧金事业项目组的各级成员恭祝各位在欧金事业里创造出更加辉煌的明天！欧金事业，创造梦想的舞台！&nbsp;</span></span></div>\r\n<div><span style=\"font-family: \'Comic Sans MS\';\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <span style=\"font-size: xx-large;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;欧金事业项目组</span></span></div>\r\n<div><span style=\"font-family: \'Comic Sans MS\';\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<span style=\"font-size: x-large;\">&nbsp;二0一四年十月五日</span></span></div>','1','1412438455','1412516496','1','0','1');-- <fen> --
INSERT INTO `xt_form` VALUES ('70','欧金事业奖励政策','<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"font-size: large;\">&nbsp;</span><span style=\"font-size: medium;\">&nbsp;亲爱的家人们！为感谢大家对我们欧金事业的支持，经公司董事会研究决定推出实物奖励方案！具体奖励如下：<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;<span style=\"color: rgb(255, 0, 0);\"> &nbsp; 凡在2014年12月31日前平衡奖达到1万美金奖励价值3000元苹果平板电脑&nbsp;ipad一台，平衡奖达到2万美金奖励价值6000元苹果手机iphone&nbsp;6一台，由报单中心审核购买发放，公司补等额货币；平衡奖达到15万美金奖宝马车一辆，请截图给公司客服审核后，由公司统一购买发放。</span><br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;以上活动最终解释归欧金事业所有。欧金事业，创造梦想的舞台！<br />\r\n</span></span></p>\r\n<p><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"font-size: medium;\">&nbsp;</span></span></p>\r\n<p><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"font-size: large;\">&nbsp; &nbsp;&nbsp;</span>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span style=\"font-size: large;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-size: large;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 欧金事业项目组</span></span></p>\r\n<p><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"font-size: large;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 二0一四年十月五日</span>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</span></p>','1','1412496344','1412516380','1','0','1');-- <fen> --
